import React from 'react'

export default class Welcome extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            firstName: '',
            lastName: '',
            email: ''
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        const name = event.target.name;
        const value = event.target.value;
        this.setState({[name]: value});
        localStorage.setItem(name, value);
    }

    onStartClick = () => {
       this.props.history.push('/game');
    }

    handleSubmit(event) {
        event.preventDefault();
    }

    nameHeader() {
        localStorage.getItem('firstName');
    }

    render(){
        return (
            <div className='welcome'>
                <h1>Welcome {this.nameHeader} to MMG</h1>

                <form id='form' className='form' onSubmit={this.handleSubmit}>
                    <label>
                        First name:
                        <input id='first' name="firstName" type='text' value={this.state.firstName} onChange={this.handleChange} />
                    </label>

                    <label>
                        Last name:
                        <input id='last' name="lastName" type='text' value={this.state.lastName} onChange={this.handleChange} />
                    </label>

                    <label>
                        Email name:
                        <input id='email' name="email" type='text' value={this.state.email} onChange={this.handleChange} />
                    </label>
                </form>

                <div className='welcome'>
                    <h3>Difficulty:</h3>
                    <select>
                        <option value='easy'>Easy</option>
                        <option value='medium'>Medium</option>
                        <option value='hard'>Hard</option>
                    </select>

                    <h3>Select card shirt:</h3>
                    <div>
                        <input type="radio" name='opt' /> Opt 1
                        <input type="radio" name='opt' /> Opt 2
                        <input type="radio" name='opt' /> Opt 3
                    </div>

                    <h3>Rules</h3>
                    <p className='rules'>
                        Lorem, ipsum dolor sit amet consectetur adipisicing elit. 
                        Veritatis quidem consequuntur quis dolor reiciendis doloremque, 
                        quas voluptate assumenda, quibusdam nobis labore error facilis 
                        enim corporis voluptatem id vel maiores molestiae.
                    </p>
                    <input type='submit' value='Start Game' form='form' onClick={this.onStartClick} />
                </div>
            </div>
        );
    }
}
