import React from 'react'




const Field_Cards_Easy = [
    {
     value: 1,
    },
    {
     value: 2,
    },
    {
     value: 3,
    },
    {
     value: 4,
    },
    {
     value: 5,
    },
    {
     value: 6,
    },
    {
     value: 7,
    },
    {
     value: 8,
    },
    {
     value: 1,
    },
    {
     value: 2,
