    {
     value: 3,
    },
    {
     value: 4,
    },
    {
     value: 5,
    },
    {
     value: 6,
    },
    {
     value: 7,
    },
    {
     value: 8,
    }  
 ];

export default class Game extends React.Component {
    constructor(){
        super();
        this.state = {};
    }

    onNewGameClick = () => {
        this.props.history.push('/welcome');
    }

    onRestartClick = () => {
        this.props.history.push('/game');
    }

    onFlipClick = () => {
        return this.value
    }



    defaultFormatMilliseconds = (x) => {
        var millis, seconds, minutes, hours;
        millis = Math.floor((x % 1000) / 100);
        x /= 1000;
        seconds = Math.floor(x % 60);
        x /= 60;
        minutes = Math.floor(x % 60);
        x /= 60;
        hours = Math.floor(x % 24);
        return [hours, minutes, seconds].join(':') + "." + millis;
    };


    render(){
        return (
            <div className='game'>
               <header>
                   <this.defaultFormatMilliseconds />
                   <div className='counter'>0</div>
               </header>
               <div className='playField'>
                   {Field_Cards_Easy.map(({value}) => (
                       <div 
                       className='card'
                       value={value}

                       onClick={this.onFlipClick}>
                       </div>
                   ))}
               </div>
               <div>
                   <button type='submit' onClick={this.onRestartClick}>RESTART</button>
                   <button type='reset' onClick={this.onNewGameClick}>new game</button>
               </div>
            </div>
        );
    }
}
