import { func } from 'prop-types';
import React from 'react'


function Timer() {
   let time = 0;
   let run = 0;

   if (run == 0) {
       setTimeout(function(){
           time++;
           let min = Math.floor(time/10/60);
           let sec = Math.floor(time/10%60);
           let hour = Math.floor(time/10/60/60);

           if (min < 10){
               min = "0" + min;
           }
           if (sec < 10){
            sec = "0" + sec;
        }
        return <div>{hour + ":" + min + ":" + sec}</div>
        Timer();
       }, 100)
   }
};


export default Timer
