const initial = {
    difficulty: '',
    cardType: ''
}

const rootReducer = (state, action) => {
    switch (action.type) {
        case CHANGE_DIFFICULTY:
            return {
                ...state, 
                difficulty: action.payload
            }

        case CHANGE_CARD_TYPE:
            return {
                ...state, 
                cardType: action.payload
            }
    }
    return state
}
