import React from 'react'

export default class IsFirstWelcome extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            firstName: '',
            lastName: '',
            email: ''
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        const name = event.target.name;
        const value = event.target.value;
        this.setState({[name]: value});
        localStorage.setItem(name, value);
    }

    handleSubmit(event) {
        event.preventDefault();
    }

    nameHeader = () => {
       let localValue = localStorage.getItem('firstName');
       return localValue;
    }

    onStartClick = () => {
        this.props.history.push('/game');
    }

    onQuitClick() {
        localStorage.clear();
    }

    render(){
        return (
            <div className='firstWelcome'>
                <h1>Welcome to MMG</h1>

                <form id='form' className='form' onSubmit={this.handleSubmit}>
                    <label>
                        First name:
                        <input id='first' className="input"  name="firstName" type='text' value={this.state.firstName} onChange={this.handleChange} />
                    </label>

                    <label>
                        Last name:
                        <input id='last' className="input" name="lastName" type='text' value={this.state.lastName} onChange={this.handleChange} />
                    </label>

                    <label>
                        Email:
                        <input id='email' className="input" name="email" type='text' value={this.state.email} onChange={this.handleChange} />
                    </label>
                </form>

                <div className='welcome'>
                    <h3>Difficulty:</h3>
                    <select className="select">
                        <option value='easy'>Easy</option>
                        <option value='medium'>Medium</option>
                        <option value='hard'>Hard</option>
                    </select>

                    <h3>Select card shirt:</h3>

                    <div className="cards">
                        <label>
                            <div className="card1">
                                <input type="radio" name='opt' />
                            </div>
                        </label>

                        <label>
                            <div className="card2">
                                <input type="radio" name='opt' />
                            </div>
                        </label>

                        <label>
                            <div className="card3">
                                <input type="radio" name='opt' />
                            </div>
                        </label>
                    </div>

                    <h3>Rules</h3>
                    <p className='rules'>
                        Lorem, ipsum dolor sit amet consectetur adipisicing elit. 
                        Veritatis quidem consequuntur quis dolor reiciendis doloremque, 
                        quas voluptate assumenda, quibusdam nobis labore error facilis 
                        enim corporis voluptatem id vel maiores molestiae.
                    </p>
                    <button className="start" type='submit' form='form' onClick={this.onStartClick}>Start Game</button>
                    <button className="quit" type='submit' form='form' onClick={this.onQuitClick}>Quit Game</button>
                </div>
            </div>
        );
    }
}
