    },
    {
     value: 4,
    },
    {
     value: 5,
    },
    {
     value: 6,
    },
    {
     value: 7,
    },
    {
     value: 8,
    }  
 ];

export default class Game extends React.Component {
    constructor(){
        super();
        this.state = {};
    }

    onNewGameClick = () => {
        this.props.history.push('/welcome');
    }

    onRestartClick = () => {
        this.props.history.push('/game');
    }

    onFlipClick = () => {
        return this.value
    }

    defaultFormatMilliseconds = () => {
        let this_date = new Date();
        let new_date = new Date() - this_date;
        let millis = Math.floor((new_date % 1000) / 100);

        let seconds = Math.floor(new_date/1000)%60;

        let minutes = Math.floor(new_date/1000/60)%60;

        let hours = Math.floor(new_date/1000/60/60)%24;
        return [hours, minutes, seconds].join(':') + "." + millis;
    }

    timer = () => {
        setTimeout(this.defaultFormatMilliseconds, 1000)
    } 


    render(){
        return (
            <div className='game'>
               <header>
                   <this.timer />
                   <div className='counter'>0</div>
               </header>
               <div className='playField'>
                   {Field_Cards_Easy.map(({value}) => (
                       <div 
                       className='card'
                       value={value}

                       onClick={this.onFlipClick}>
                       </div>
                   ))}
               </div>
               <div>
                   <button type='submit' onClick={this.onRestartClick}>RESTART</button>
                   <button type='reset' onClick={this.onNewGameClick}>new game</button>
               </div>
            </div>
        );
    }
}
