import React from 'react'


class Timer extends React.Component {
    constructor(props) {
      super(props);
      this.state = {date: new Date()};
    }

    componentDidMount() {
      this.timerID = setInterval(
        () => this.tick(),
        1000
      );
    }

    componentWillUnmount() {
      clearInterval(this.timerID);
    }

    tick() {
      this.setState({
        date: new Date() 
      });
    }

    render() {
      return (
        <div>
         {this.state.date.setHours(0)}
        </div>
      );
    }
  }


export default Timer
