import React from 'react'

export default class Welcome extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            first: '',
            last: '',
            email: ''
        };

        this.handleFirstChange = this.handleFirstChange.bind(this);
        this.handleLastChange = this.handleLastChange.bind(this);
        this.handleEmailChange = this.handleEmailChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleFirstChange(event) {
        this.setState({first: event.target.first,});
    }

    handleLastChange(event) {
        this.setState({last: event.target.last});
    }

    handleEmailChange(event) {
        this.setState({email: event.target.email});
    }


    store(){
       localStorage.setItem('first', this.state.first);
    }

    handleSubmit(event) {
        event.preventDefault();
    }

    render(){
        return (
            <div className='welcome'>
                <h1>Welcome to MMG</h1>

                <form id='form' className='form' onSubmit={this.handleSubmit}>
                    <label>
                        First name:
                        <input id='first' type='text' value={this.state.first} onChange={this.handleFirstChange} />
                    </label>

                    <label>
                        Last name:
                        <input id='last' type='text' value={this.state.last} onChange={this.handleLastChange} />
                    </label>

                    <label>
                        Email name:
                        <input id='email' type='text' value={this.state.email} onChange={this.handleEmailChange} />
                    </label>
                </form>

                <div className='welcome'>
                    <h3>Difficulty:</h3>
                    <select>
                        <option value='easy'>Easy</option>
                        <option value='medium'>Medium</option>
                        <option value='hard'>Hard</option>
                    </select>

                    <h3>Select card shirt:</h3>
                    <div>
                        <input type="radio" name='opt' /> Opt 1
                        <input type="radio" name='opt' /> Opt 2
                        <input type="radio" name='opt' /> Opt 3
                    </div>

                    <h3>Rules</h3>
                    <p className='rules'>
                        Lorem, ipsum dolor sit amet consectetur adipisicing elit. 
                        Veritatis quidem consequuntur quis dolor reiciendis doloremque, 
                        quas voluptate assumenda, quibusdam nobis labore error facilis 
                        enim corporis voluptatem id vel maiores molestiae.
                    </p>
                    <input type='submit' value='Start Game' form='form' onClick={this.store} />
                </div>
            </div>
        );
    }
}
