import React from 'react'
import Card1 from '../../../common/Card1'

import _ from 'lodash/wrapperLodash'


export default class Field extends React.Component {
    constructor(props){
        super(props)
        this.state = {field: this.generateFields()}
    }


    generateFields(difficulty = 4) {
        let valueOfCard = ['A', 'B', 'C', 'D', 'E', 'F', 'G', 'H']
        let numberOfFields = difficulty * difficulty
        let cardsValues = []
        let j = 0

        for(let i=0; i < numberOfFields; i=i+2) {
            let card = valueOfCard[j]
            cardsValues.push(card, card)

            if(j === valueOfCard.length - 1){
                j = 0
            } else {
                j++
            }
        }

        let shuffle = require('lodash.shuffle')
        cardsValues = _.shuffle(cardsValues)

        let k = 0
        let fields = []
        for (let i = 0; i < difficulty; i++) {
            fields[i] = []
            for (let j = 0; j < difficulty; j++) {
                fields[i].push({
                    value: cardsValues[k],
                    flipped: false,
                    matched: false
                })
                k++
            }
        }
        return fields
    }


    render(){
        return(
        <div className='cards'>{this.state.field.map((row, rowIndex) => row.map((col, colIndex) => <Card1 row={rowIndex} col={colIndex} />))}</div>   
        )
    }
}
