import React from 'react'
import Timer from './components/Timer'




const Field_Cards_Easy = [
    {
     value: 1,
    },
    {
     value: 2,
    },
    {
     value: 3,
    },
    {
     value: 4,
    },
    {
     value: 5,
    },
    {
     value: 6,
    },
    {
     value: 7,
    },
    {
     value: 8,
    },
    {
     value: 1,
    },
    {
     value: 2,
    },
    {
     value: 3,
    },
    {
     value: 4,
    },
    {
     value: 5,
    },
    {
     value: 6,
    },
    {
     value: 7,
    },
    {
     value: 8,
    }  
 ];

export default class Game extends React.Component {
    constructor(){
        super();
        this.state = {};
    }

    onNewGameClick = () => {
        this.props.history.push('/welcome');
    }

    onRestartClick = () => {
        this.props.history.push('/game');
    }

    onFlipClick = () => {
        return this.value
    }


    render(){
        return (
            <div className='game'>
               <header>
                   <Timer />
                   <div className='counter'>0</div>
               </header>(function() {
    setInterval(function(){
        let this_date = new Date();
        let new_date = new Date() - this_date;
        let millis = Math.abs(Math.floor((new_date % 1000) / 100));

        let seconds = Math.abs(Math.floor(new_date / 1000) % 60);

        let minutes = Math.abs(Math.floor(new_date / 1000 / 60) % 60);

        let hours = Math.abs(Math.floor(new_date / 1000 / 60 / 60) % 24);

        return <div>{[hours, minutes, seconds].join(':') + "." + millis}</div>
    })

})();
               <div className='playField'>
                   {Field_Cards_Easy.map(({value}) => (
                       <div 
                       className='card'
                       value={value}

                       onClick={this.onFlipClick}>
                       </div>
                   ))}
               </div>
               <div>
                   <button type='submit' onClick={this.onRestartClick}>RESTART</button>
                   <button type='reset' onClick={this.onNewGameClick}>new game</button>
               </div>
            </div>
        );
    }
}
