import React from 'react'
import Card1 from '../../../common/Card1';


export default class Field extends React.Component {
    constructor(props){
        super(props);
        this.state = {fields: this.generateFields()}
    }


    generateFields(difficulty = 4) {
        const valueOfCard = ['A', 'B', 'C', 'D']
        const numberOfFields = difficulty * difficulty
        const cardsValues = []
        const j = 0

        for(let i=0; i < numberOfFields; i=i+2) {
            const card = valueOfCard[j]
            cardsValues.push(card, card)

            if(j === valueOfCard.length - 1){
                j = 0
            } else {
                j++
            }
        }

        const k = 0
        const fields = []
        for (let i = 0; i < difficulty; i++) {
            fields[i] = []
            for (let j = 0; j < difficulty; j++) {
                fields[i].push({
                    value: cardsValues[k],
                    flipped: false,
                    matched: false
                })
                k++
            }
        }
        return fields
    }



    render(){
        return(
            <Card1 />
        )
    }
}
