    },
    {
     value: 8,
    }  
 ];

export default class Game extends React.Component {
    constructor(){
        super();
        this.state = {};
    }

    onNewGameClick = () => {
        this.props.history.push('/welcome');
    }

    onRestartClick = () => {
        this.props.history.push('/game');
    }

    onFlipClick = () => {
        return this.value
    }

    startTime = () => {

    var this_date = new Date();
    function time(){
    var new_date = new Date() - this_date;
    var sec = Math.abs(Math.floor(new_date/1000)%60); 
    var min = Math.abs(Math.floor(new_date/1000/60)%60); 
    var hours = Math.abs(Math.floor(new_date/1000/60/60)%24); 
    if (sec.toString().length === 1) sec = '0' + sec;
    if (min.toString().length === 1) min = '0' + min;
    if (hours.toString().length === 1) hours = '0' + hours;
    };

    var startTime_interval = setInterval(1,100);
    clearInterval(startTime_interval);


    return <div>{startTime_interval}</div>
    };

    render(){
        return (
            <div className='game'>
               <header>
                   <this.startTime />
                   <div className='counter'>0</div>
               </header>
               <div className='playField'>
                   {Field_Cards_Easy.map(({value}) => (
                       <div 
                       className='card'
                       value={value}

                       onClick={this.onFlipClick}>
                       </div>
                   ))}
               </div>
               <div>
                   <button type='submit' onClick={this.onRestartClick}>RESTART</button>
                   <button type='reset' onClick={this.onNewGameClick}>new game</button>
               </div>
            </div>
        );
    }
}
